/* Create and populate reference look-up table for
 * Truven Service Type
 * Table: ccaes
 * Field: svcscat
 * Ref table: truven.ref_service_type
 * DW table: medical
 * DW field: svc_type
 * Note: Service Type:  (Surgery, Maternity, etc)
*/

BEGIN TRANSACTION ISOLATION LEVEL SERIALIZABLE;

drop table if exists truven.ref_service_type;

CREATE TABLE truven.ref_service_type (
	"key" int4 NULL,
	value varchar NULL
)
DISTRIBUTED BY ("key");


commit;

-- Test join
select distinct lu.value, t.svcscat
from truven.ccaes as t 
join truven.ref_service_type lu on (t.svcscat = lu."key")
limit 100;